mapper.select:
select a.*, (select b.name FROM dinky_cluster b where b.id=a.cluster_id) as clusterName, (select c.name FROM dinky_task c where c.id=a.task_id) as jobName from dinky_history a
1=1 and a.task_id = #{param.taskId}
and a.job_id = #{param.jobId}
and a.job_name like "%${param.jobName}%"
and a.cluster_id = #{param.clusterId}
and a.status = #{param.status}
and a.start_time >= str_to_date( #{param.startTime[0]},'%Y-%m-%d %H:%i:%s') and a.start_time <= str_to_date( #{param.startTime[1]},'%Y-%m-%d %H:%i:%s')
and a.end_time >= str_to_date( #{param.endTime[0]},'%Y-%m-%d %H:%i:%s') and a.end_time <= str_to_date( #{param.endTime[1]},'%Y-%m-%d %H:%i:%s')
and
${ew.sqlSegment}
