mapper.select:



        AND `name` like concat('%', #{name}, '%')


        AND display = #{display}


order by parent_id, order_num

select distinct m.id, m.parent_id, m.`name`, m.path, m.component, m.display, ifnull(m.perms,'') as perms, m.type, m.icon, m.order_num, m.create_time from dinky_sys_menu m left join dinky_role_menu rm on m.id = rm.menu_id left join dinky_user_role ur on rm.role_id = ur.role_id left join dinky_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND m.`name` like concat('%', #{name}, '%')
AND m.display = #{display}
order by m.parent_id, m.order_num
